import axios from "axios";

const API_KEY = "4L899V8QWQXTQP85"; // Replace with your Alpha Vantage API key
const API_BASE_URL = "https://www.alphavantage.co/query";

// Method to fetch overview information for a specific stock symbol
export const fetchStockOverview = async (symbol) => {
  try {
    const response = await axios.get(API_BASE_URL, {
      params: {
        function: "OVERVIEW",
        symbol: symbol,
        apikey: API_KEY,
      },
    });

    return response.data;
  } catch (error) {
    handleApiError(error); // Call the error handler function
    throw error; // Re-throw the error to allow components to handle it
  }
};

// Error handler function
const handleApiError = (error) => {
  if (error.response) {
    // The request was made and the server responded with a status code
    console.error("API Error Response:", error.response.data);
    console.error("Status Code:", error.response.status);
    console.error("Headers:", error.response.headers);
  } else if (error.request) {
    // The request was made but no response was received
    console.error("API Request Error:", error.request);
  } else {
    // Something happened in setting up the request that triggered an error
    console.error("API Error:", error.message);
  }
};
