import axios from "axios";

const API_KEY = "135e0789b5c24be6a9fcf196a28ab290"; // Replace with your Twelve Data API key
const API_BASE_URL = "https://api.twelvedata.com";

// Method to fetch stock details for a specific symbol
export const fetchStockDetails = async (symbol) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/quote`, {
      params: {
        symbol: symbol,
        apikey: API_KEY,
      },
    });

    return response.data;
  } catch (error) {
    handleApiError(error);
    throw error;
  }
};

// Error handler function
const handleApiError = (error) => {
  if (error.response) {
    const errorData = error.response.data;
    console.error("API Error Response:", errorData.message);
    console.error("Status Code:", errorData.code);
    console.error("Status:", errorData.status);
  } else if (error.request) {
    console.error("API Request Error:", error.request);
  } else {
    console.error("API Error:", error.message);
  }
};

export default {
  fetchStockDetails,
};
